    public  String StringFilter(String str) throws PatternSyntaxException {
        // 只允许字母和数字、中文
       //  String regEx="[^a-zA-Z0-9]";
         String regEx="[^0-9]";
        // 清除掉[]中所有特殊字符
//        String regEx = "[`~☆★!@#$%^&*()+=|{}':;,\\[\\]》·.<>/?~！@#￥%……（）——+|{}【】‘；：”“’。，、？]";
        Pattern p = Pattern.compile(regEx);
        Matcher m = p.matcher(str);
        return m.replaceAll("").trim().replace(" ", "").replace("\\", "");
    }


